import React from 'react';

class Body extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            answer: "2日後です",
            open: false
        }
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(){
        this.setState({open: true})
    }

    render(){
        return(
            <div>
                <div class="title"></div>
                <div class="questions">
                    <p>注文してからどのくらいで届きますか？
                        <butoon onClick={this.handleClick}>▼</butoon>
                        <p>{this.state.open}</p>
                    </p>
                    <p>配送業者を指定できますか？</p>
                    <p>送料はかかりますか？</p>
                    <p>返品は可能ですか？</p>
                    <p></p>
                </div>
            </div>
        );
    }
}

export default Body;
